mapper.update:

UPDATE whale_repu
SET `repu_star_cnt` = #{repu_star_cnt}
	, `upd_dt` = SYSDATE()
	, `upd_id` = 'system'
WHERE 
	`to_user_id` = #{to_user_id}
	AND
	`from_user_id` = #{from_user_id}
	AND  
	`repu_type_cd` = #{repu_type_cd};

mapper.select:

SELECT 
	SUM(`repu_star_cnt`) AS avr_star_cnt
FROM 
	whale_repu
WHERE `to_user_id` = #{to_user_id}
AND `repu_type_cd` = #{repu_type_cd}


SELECT 
	COUNT(`from_user_id`) AS from_user_cnt
FROM whale_repu
WHERE `to_user_id` = #{to_user_id}
AND 
`repu_type_cd` = #{repu_type_cd}

mapper.update:

UPDATE whale_tot_repu
SET `avr_star_cnt` = #{avr_star_cnt}
	,`from_user_cnt` = #{from_user_cnt}
	,`upd_dt` = SYSDATE()
	,`upd_id` = 'SYSTEM'
WHERE 
	`user_id` = #{to_user_id}
	AND
	`repu_type_cd` = #{repu_type_cd}
